sys_ui_script.script:
var IndicatorDefinitionSelector = createReactClass({
	getInitialState: function() {
		return { indicatordefinition: null, loading: false, indicatordefinitions: [] };
	},

	onSearch: function(query) {
		var that = this;
		that.setState({ loading: true });

		var searcher = {};
		searcher.filter = query;
		searcher.maxresult = 50;

		sn_kpi_composer.ClientApi.selectIndicatorDefinitions(
			searcher,
			function (result) {
				if (result.success) {
					for (var i = 0; i < result.indicator_definitions.length; i++) {
						if (result.indicator_definitions[i].name == null) {
							result.indicator_definitions[i].name = "(no name specified)";
						}
					}
					that.setState({ indicatordefinitions: result.indicator_definitions, loading: false });
				} else {
					that.setState({ loading: false });	
				}
			},
			function (error) {
				that.setState({ loading: false });
			}
		);
	},

	onChange: function(selected) {
		if (selected.length > 0) {
			var that = this;
			this.props.onIndicatorDefinitionSelected(selected[0], function(error) {
				if (error == null) {
					if (that.refs.typeahead != null) that.refs.typeahead.getInstance().clear();	
				}
			});	
		}

	},

	renderMenuItemChildren: function(option, props, index) {
		return [
			e(
				'div',
				{ key: 'number' },
				option.name
			),
			e(
				'div',
				{ key: 'short_description' },
				option.description
			)
		];
	},

	filterBy: function() {
		return true;
	},

	render: function() {
		return e(
			ReactBootstrapTypeahead.AsyncTypeahead,
			{
				ref: 'typeahead',
				isLoading: this.state.loading,
				minLength: 0,
				labelKey: 'name',
				filterBy: this.filterBy,
				onSearch: this.onSearch,
				onChange: this.onChange,
				onBlur: this.props.onBlur,
				renderMenuItemChildren: this.renderMenuItemChildren,
				options: this.state.indicatordefinitions,
				disabled: this.props.disabled,
				placeholder: this.props.placeholder,
				selectHintOnEnter: true,
				autoFocus: true,
				//bodyContainer: true, //bij rtl komt dit niet goed, daarom positionFixed gebruiken
				positionFixed: true,
				useCache: false, //werkt niet goed,
				promptText: getMessage('Type to search...'),
				emptyLabel: getMessage('No matches found.')

			}
		);
	}
});
