sys_ui_script.script:
var OverviewProjects = createReactClass({
	render: function() {
		var that = this;

		var projects = this.props.projects.map(
			function (project) {
				return e(
					OverviewProject,
					{
						key: project.sys_id,
						project: project,
						returnUrl: that.props.returnUrl,
						onProjectDelete: that.props.onProjectDelete
						//gaat nu via href
						//, onProjectSelected: that.props.onProjectSelected
					}
				);
			}
		);

		if (projects.length == 0) {
			projects.push(
				e(
					'h4',
					{ key: 'empty' },
					getMessage("No projects available")
				)
			);
		}

		return e(
			'div',
			{ className: 'pa-overview-cards-container' },
			projects
		);
	}
});
