sys_ui_script.script:
var DashboardTabAddTemplateItem = createReactClass({
	onClick: function (evt) {
        evt.stopPropagation();
        evt.preventDefault();
        this.props.onSelected(this.props.templateitem);
    },

    componentDidMount: function() {
        if (this.props.isselected && this.refs.thelink && this.props.doautoselect) {
            var thelink = this.refs.thelink;
            setTimeout(function(){thelink.focus();}, 100);
        }
    },

	render: function() {
        var that = this;

        var icontablerows = [];
        if (this.props.templateitem.columns_per_row) {
            for (var r = 0; r < this.props.templateitem.columns_per_row.length; r++) {
                var cols = [];
                var nrofcols = this.props.templateitem.columns_per_row[r];
                for (var c = 0; c < nrofcols; c++) {
                    cols.push(e(
                        "span",
                        {key: c}
                    ));
                }
                var nrofcolsclassname = "";
                if (nrofcols === 1) {
                    nrofcolsclassname = "one";
                } else if (nrofcols === 2) {
                    nrofcolsclassname = "two";
                } else if (nrofcols === 3) {
                    nrofcolsclassname = "three";
                }
                cols.push(e(
                    "div",
                    {key: "clearfix", className: "clearfix"}
                ));
                icontablerows.push(e(
                    "div",
                    {key: r, className: nrofcolsclassname },
                    cols
                ));
            }
        } else {
            icontablerows.push(e(
                "div",
                {key: "blank", className: "blank" },
                e(
                    "span"
                ),
                e(
                    "div",
                    {className: "clearfix"}
                )
            ));
        }

		return e(
			'a',
			{ className: 'list-group-item' + (this.props.isselected ? ' active' : ''), onClick: this.onClick, href: window.location.href, ref: 'thelink' },
			e(
				'div',
				{ className: 'pull-left dashboardtabtemplateicon' },
				e(
                    "div",
                    null,
                    icontablerows
                )
            ),
            e(
				'div',
				{ className: 'pull-left dashboardtabtemplatetext' },
                this.props.templateitem.caption,
                e(
                    'div',
                    {className: 'small'},
                    this.props.templateitem.description
                )
			),
			e(
				'span',
				{ className: 'pull-right icon icon-arrow-right dashboardtabtemplatebuttonicon' }
            ),
            e(
                'div',
                {className: 'clearfix' }
            )
		);
	}
});
