sys_ui_script.script:
var OverviewTab = createReactClass({
	tabChanged: function() {
		this.props.onTabChanged(this.props.tab.id);
	},

	render: function() {
		var that = this;
		return e(
            'button',
            { className: 'btn pa-overview-tab' + (this.props.selected ? ' pa-overview-selected-tab' : ''), "aria-selected": "true", tabIndex: -1, role: "tab", onClick: this.tabChanged },
            this.props.tab.label
        );
	}
});
