sys_ui_script.script:
var DraggableContainer = createReactClass({
	render: function() {
		var that = this;

		return this.props.datalist.map(
			function (item) {
				return e(
					DraggableItem,
					{
						key: item.sys_id,
						item: item,
						category: that.props.category,
						displaytype: that.props.displaytype,
						type: that.props.type,
						selected: that.props.selectedids != null ? that.props.selectedids.indexOf(item.sys_id) > -1: false,
						onClick: that.props.onSelectedChange,
						disableDragging: that.props.disableDragging
					}
				);
			}
		);
	}
});
