sys_ui_script.script:
var OverviewLoading = createReactClass({
	getDefaultProps: function() {
		return {
			title: getMessage('Loading Data...'),
			show: true
		};
	},

	render: function() {
		if (this.props.show === false) return null;

		return e(
			'div',
			{ className: 'pa-loading pa-loading-overview' },
			e(
				'div',
				{className: 'icon-loading'}
			),
			e(
				'div',
				{className: 'pa-loading-text'},
				this.props.title
			)
		);
	}
});
