sys_ui_script.script:
var OverviewHeader = createReactClass({
	getInitialState: function() {
		return {
			addnew: false
		};
	},

	createNew: function() {
		this.setState({ addnew: true });
	},

	onProjectAddSave: function(name) {
		this.props.onProjectAdd(name);
	},

	onProjectAddCancel: function() {
		this.setState({ addnew: false });
	},

	render: function() {
		var that = this;

		var addnew = null;
		if (this.state.addnew == true) {
			addnew = e(
				OverviewAddProject,
				{ key: 'addmodal', onSave: this.onProjectAddSave, onCancel: this.onProjectAddCancel }
			);
		}

		var createbutton = null;
		if (!this.props.library && this.props.onProjectAdd) {
			createbutton = e(
				"button",
				{ className: "btn btn-primary align-vertical sn-tooltip-basic", "aria-label": getMessage("Create New Project"), title: getMessage("Create New Project"), role: 'button', onClick: that.createNew },
				getMessage("New")
			);
		}

		var pageTitle;
		if(this.props.library)
			pageTitle = getMessage("Library Element");
		else
			pageTitle = getMessage("Project");

		return [
			e(
				'div',
				{ className: "pa-overview-header", tabIndex: 0, "aria-label": getMessage("Projects overview header"), key: 'header' },
				e(
					'div',
					{ role: 'heading', 'aria-level': '1', className: 'title', tabIndex: 0 },
					pageTitle
				),
				createbutton
			),
			addnew
		];
	}
});
